import React, { useState, useEffect } from "react";
import io from "socket.io-client";

// Connect with authentication
const socket = io("http://localhost:5000", {
  auth: {
    token: localStorage.getItem("token") || null,
  },
  reconnectionAttempts: 5,
  reconnectionDelay: 1000,
});

function App() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [currentRoom, setCurrentRoom] = useState("general");
  const [roomUsers, setRoomUsers] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // Authentication handlers
  const handleRegister = async () => {
    setIsLoading(true);
    setError("");
    try {
      const response = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Registration failed");
      }

      const data = await response.json();
      localStorage.setItem("token", data.token);
      setIsAuthenticated(true);
      socket.auth.token = data.token;
      socket.connect();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogin = async () => {
    setIsLoading(true);
    setError("");
    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Login failed");
      }

      const data = await response.json();
      localStorage.setItem("token", data.token);
      setIsAuthenticated(true);
      socket.auth.token = data.token;
      socket.connect();
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Socket event handlers
  useEffect(() => {
    if (!isAuthenticated) return;

    const onConnect = () => {
      console.log("Connected to server");
      socket.emit("join-room", currentRoom, (response) => {
        if (response.error) {
          console.error("Room join error:", response.error);
        }
      });
    };

    const onNewMessage = (msg) => {
      setMessages((prev) => [...prev, msg]);
    };

    const onUserJoined = (data) => {
      setMessages((prev) => [
        ...prev,
        {
          system: true,
          message: `${data.username} joined the room`,
          timestamp: data.timestamp,
        },
      ]);
    };

    const onUserLeft = (data) => {
      setMessages((prev) => [
        ...prev,
        {
          system: true,
          message: `${data.username} left the room`,
          timestamp: data.timestamp,
        },
      ]);
    };

    const onRoomInfo = (data) => {
      setMessages(data.messages);
      setRoomUsers(data.users);
    };

    const onConnectError = (err) => {
      console.error("Connection error:", err);
      if (err.message === "Authentication failed") {
        localStorage.removeItem("token");
        setIsAuthenticated(false);
        setError("Session expired. Please login again.");
      }
    };

    socket.on("connect", onConnect);
    socket.on("new-message", onNewMessage);
    socket.on("user-joined", onUserJoined);
    socket.on("user-left", onUserLeft);
    socket.on("room-info", onRoomInfo);
    socket.on("connect_error", onConnectError);

    return () => {
      socket.off("connect", onConnect);
      socket.off("new-message", onNewMessage);
      socket.off("user-joined", onUserJoined);
      socket.off("user-left", onUserLeft);
      socket.off("room-info", onRoomInfo);
      socket.off("connect_error", onConnectError);
    };
  }, [isAuthenticated, currentRoom]);

  // Handle room change
  const handleRoomChange = (newRoom) => {
    setCurrentRoom(newRoom);
    if (isAuthenticated) {
      socket.emit("join-room", newRoom, (response) => {
        if (response.error) {
          console.error("Room change error:", response.error);
        }
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim() && isAuthenticated) {
      socket.emit(
        "send-message",
        {
          room: currentRoom,
          message,
        },
        (response) => {
          if (response.error) {
            console.error("Message send error:", response.error);
          } else {
            setMessage("");
          }
        }
      );
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    setUsername("");
    setPassword("");
    socket.disconnect();
  };

  if (!isAuthenticated) {
    return (
      <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
        <h1>Chat Login</h1>
        {error && (
          <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>
        )}
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ width: "100%", padding: "8px" }}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: "100%", padding: "8px" }}
          />
        </div>
        <div style={{ display: "flex", gap: "10px" }}>
          <button
            onClick={handleLogin}
            disabled={isLoading}
            style={{ padding: "8px 16px" }}
          >
            {isLoading ? "Loading..." : "Login"}
          </button>
          <button
            onClick={handleRegister}
            disabled={isLoading}
            style={{ padding: "8px 16px" }}
          >
            {isLoading ? "Loading..." : "Register"}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <h1>Real-Time Chat - {currentRoom}</h1>
        <button onClick={handleLogout} style={{ padding: "5px 10px" }}>
          Logout
        </button>
      </div>

      <div style={{ marginBottom: "10px" }}>
        <select
          value={currentRoom}
          onChange={(e) => handleRoomChange(e.target.value)}
          style={{ padding: "8px" }}
        >
          <option value="general">General</option>
          <option value="gaming">Gaming</option>
          <option value="tech">Tech</option>
        </select>
      </div>

      <div
        style={{
          height: "400px",
          overflowY: "scroll",
          border: "1px solid #ccc",
          padding: "10px",
          marginBottom: "10px",
          backgroundColor: "#f9f9f9",
        }}
      >
        {messages.map((msg, i) => (
          <div key={i} style={{ marginBottom: "8px" }}>
            {msg.system ? (
              <em style={{ color: "#666" }}>{msg.message}</em>
            ) : (
              <p>
                <strong>{msg.username}:</strong> {msg.message}
                {msg.timestamp && (
                  <small style={{ color: "#999", marginLeft: "10px" }}>
                    {new Date(msg.timestamp).toLocaleTimeString()}
                  </small>
                )}
              </p>
            )}
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} style={{ display: "flex", gap: "10px" }}>
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          style={{ flex: 1, padding: "8px" }}
        />
        <button type="submit" style={{ padding: "8px 16px" }}>
          Send
        </button>
      </form>

      <div style={{ marginTop: "20px" }}>
        <h3>Users in room:</h3>
        <ul style={{ listStyle: "none", padding: 0 }}>
          {roomUsers.map((user, i) => (
            <li key={i} style={{ padding: "4px 0" }}>
              {user.username}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
